import React from "react";
import { useState } from "react";

function MyBooks({books, currentUser, returnBook}) {

    const [ myCheckedOutBooks, setMyCheckedOutBooks ] = useState( [] )
    const [ checkInAction, setcheckInAction ] = useState( false )

    const myBooks = books.filter(book => {
        if (book.owner === currentUser && currentUser !== "") {
            return book;
        } else {
            return false;
        }
    })

    function handleReturnBookClick(book) {

        const updatedBooks = books.map(bookObj => {
            if ((bookObj.id) === (book.id)) {
              bookObj.owner = currentUser;
              return bookObj;
            } else {
              return bookObj;
            }
          });

           fetch(`http://localhost:6001/books/${book.id}`, {
              method: 'PATCH',
              headers: {
                'Content-type': 'application/json',
              },
              body: JSON.stringify({
                owner: currentUser
            }),
            });

            setBooks(updatedBooks);



        //returnBook(book);
        setMyCheckedOutBooks(myBooks);
        setcheckInAction(true);
    }

    const bookList = myBooks.map(book => {
        return (
            <div key={book.id}>
                <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2">{book.title}</span>
                <button onClick={() => handleReturnBookClick(book)}>Return Book</button>
            </div>
        )
    })


    return (
    <div>
        <h1>My Books</h1>

        { (myBooks.length > 0) ? (
            <div>
                <div>Your checked out books:</div>
                <div>{bookList}</div>
            </div>
          ) : (
            <div>You have no books checked out</div>
          )}
    </div>
  );
}

export default MyBooks;
