import React from "react";

function Search({ changeSearchTerm }) {

  const handleSearch = e => changeSearchTerm( e.target.value )
  const handleGenreSelect = e => handleGenreSelect( e.target.value )

  return (
    <div className="flex justify-end 
    py-2 px-6 bg-gray-30 border-b rounded-l">
      <h2 className="font-bold px-3 py-2">Search Books:</h2>
      <input
        type="text"
        id="search"
        placeholder="Type a title to search..."
        onChange={ handleSearch }
        className="px-3 py-2 font-semibold placeholder-gray-500 text-black rounded-2xl
        border-none ring-2 ring-gray-300 focus:ring-gray-500 focus:ring-2"
      />
      <input
        type="dropdown"
        id="genre"
        placeholder="Select a genre..."
        onChange={ handleGenreSelect }
        className="px-3 py-2 font-semibold placeholder-gray-500 text-black rounded-2xl
        border-none ring-2 ring-gray-300 focus:ring-gray-500 focus:ring-2"
      />
    </div>
  );
} 

export default Search;
